CREATE TABLE IF NOT EXISTS "account" (
  "account_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "profile_pic_name" varchar(30),
  "profile_pic" bytea,
  "first_name" varchar(30) NOT NULL,
  "last_name" varchar(30) NOT NULL,
  "email" varchar NOT NULL,
  "password_hash" varchar NOT NULL,
  "salt" varchar,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "brand" (
  "brand_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "name" varchar(30) NOT NULL,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "subscription" (
  "subscription_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "brand_id" int NOT NULL,
  "account_id" int NOT NULL,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "sale_item" (
  "sale_item_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "brand_id" int NOT NULL,
  "name" varchar(30) NOT NULL,
  "description" text,
  "item_variation_id" int NOT NULL,
  "url"
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "size" (
  "size_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "name" varchar(15) NOT NULL,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "gender" (
  "gender_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "name" varchar(10) NOT NULL,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "clothing_article" (
  "clothing_article_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "name" varchar(25) NOT NULL,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "color" (
  "color_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "name" varchar(25) NOT NULL,
  "color_category_id" int,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "color_category" (
  "color_category_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "color" varchar(20) NOT NULL,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "item_variation" (
  "item_variation_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "color_id" int,
  "clothing_article_id" int,
  "gender_id" int,
  "size_id" int,
  "sale_price" money NOT NULL,
  "original_price" money,
  "is_sale" bool NOT NULL DEFAULT true,
  "image_name" varchar(30),
  "image" bytea,
  "sale_end_time" timestamp DEFAULT null,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "list" (
  "list_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "list_image_name" varchar(30),
  "list_image" bytea,
  "name" varchar(25),
  "description" text,
  "account_id" int,
  "create_time" timestamp DEFAULT (now()),
  "update_time" timestamp DEFAULT (now())
);

CREATE TABLE IF NOT EXISTS "list_item" (
  "list_item_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "list_id" int NOT NULL,
  "sale_item_id" int NOT NULL
);

ALTER TABLE "subscription" ADD FOREIGN KEY ("brand_id") REFERENCES "brand" ("brand_id");

ALTER TABLE "subscription" ADD FOREIGN KEY ("account_id") REFERENCES "account" ("account_id");

ALTER TABLE "sale_item" ADD FOREIGN KEY ("brand_id") REFERENCES "brand" ("brand_id");

ALTER TABLE "sale_item" ADD FOREIGN KEY ("item_variation_id") REFERENCES "item_variation" ("item_variation_id");

ALTER TABLE "color" ADD FOREIGN KEY ("color_category_id") REFERENCES "color_category" ("color_category_id");

ALTER TABLE "item_variation" ADD FOREIGN KEY ("color_id") REFERENCES "color" ("color_id");

ALTER TABLE "item_variation" ADD FOREIGN KEY ("clothing_article_id") REFERENCES "clothing_article" ("clothing_article_id");

ALTER TABLE "item_variation" ADD FOREIGN KEY ("gender_id") REFERENCES "gender" ("gender_id");

ALTER TABLE "item_variation" ADD FOREIGN KEY ("size_id") REFERENCES "size" ("size_id");

ALTER TABLE "list" ADD FOREIGN KEY ("account_id") REFERENCES "account" ("account_id");

ALTER TABLE "list_item" ADD FOREIGN KEY ("list_id") REFERENCES "list" ("list_id");

ALTER TABLE "list_item" ADD FOREIGN KEY ("sale_item_id") REFERENCES "sale_item" ("sale_item_id");


